<style>
	sidebar {
		display: flex;
		flex-direction: column;
		margin-top: 12px;
		justify-content: space-between;
	}

	sidebar::-webkit-scrollbar {
		display: none;
	}

	sidebar ul {
		display: flex;
		flex-direction: column;
		padding-inline-start: 0px;
		overflow-y: scroll;
		scrollbar-width: none;
	}

	sidebar ul::-webkit-scrollbar {
