	}

	selectedhomeicon {
		display: flex;
		flex-direction: row;
	}

	selectediconindicator {
		width: 3px;
		margin-left: 2px;
		border-radius: 99px;
		background-color: red;
		height: 60%;
		align-self: center;
	}

	homeicon {
		border: 1px solid white;
		padding: 8px 10px 8px 0px;
		background-color: white;
		border-radius: 0px 12px 12px 0px;
		box-shadow: 2px 2px 5px 0px rgb(225, 223, 221);
	}

	iconframe {
		display: flex;

		align-items: center;
		height: 100%;
		width: 80%;
		padding: 10px 6px 10px 0px;
		margin-top: 4px;
	}

	iconframe:hover {
		background-color: rgb(225, 223, 221);
		border-radius: 0px 12px 12px 0px;
	}

	iconframe .applogo {
		width: 20px;
		margin-left: 12px;
	}

	divider {
		border-bottom: 1px solid rgb(214, 213, 212);
